/*==================================================
NewStudentView.js

The Views component is responsible for rendering the web page with data provided by the corresponding Container component.
It constructs a React component to display the new student page.
================================================== */
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";

// Create styling for the input form
const useStyles = makeStyles(() => ({
  formContainer: {
    width: "500px",
    backgroundColor: "#f0f0f5",
    borderRadius: "5px",
    margin: "auto",
    padding: "20px",
  },
  title: {
    flexGrow: 1,
    textAlign: "left",
    textDecoration: "none",
  },
  customizeAppBar: {
    backgroundColor: "#11153e",
    shadows: ["none"],
  },
  formTitle: {
    backgroundColor: "#c5c8d6",
    marginBottom: "15px",
    textAlign: "center",
    borderRadius: "5px 5px 0px 0px",
    padding: "3px",
  },
  errorMessage: {
    color: "red",
    fontSize: "0.9em",
  },
}));

const NewStudentView = (props) => {
  const { handleChange, handleSubmit, errors, studentData } = props;
  const classes = useStyles();

  // Render a New Student view with an input form
  return (
    <div>
      <h1>New Student</h1>

      <div className={classes.root}>
        <div className={classes.formContainer}>
          <div className={classes.formTitle}>
            <Typography
              style={{
                fontWeight: "bold",
                fontFamily: "Courier, sans-serif",
                fontSize: "20px",
                color: "#11153e",
              }}
            >
              Add a Student
            </Typography>
          </div>
          <form style={{ textAlign: "center" }} onSubmit={(e) => handleSubmit(e)}>
            {/* First Name */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>First Name: </label>
            <input
              type="text"
              name="firstname"
              value={studentData.firstname}
              onChange={(e) => handleChange(e)}
            />
            {errors.firstname && <p className={classes.errorMessage}>{errors.firstname}</p>}
            <br />
            <br />

            {/* Last Name */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>Last Name: </label>
            <input
              type="text"
              name="lastname"
              value={studentData.lastname}
              onChange={(e) => handleChange(e)}
            />
            {errors.lastname && <p className={classes.errorMessage}>{errors.lastname}</p>}
            <br />
            <br />

            {/* Email */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>Email: </label>
            <input
              type="email"
              name="email"
              value={studentData.email}
              onChange={(e) => handleChange(e)}
            />
            {errors.email && <p className={classes.errorMessage}>{errors.email}</p>}
            <br />
            <br />

            {/* Image URL */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>Image URL: </label>
            <input
              type="text"
              name="imageUrl"
              value={studentData.imageUrl}
              onChange={(e) => handleChange(e)}
            />
            <br />
            <br />

            {/* GPA */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>GPA: </label>
            <input
              type="number"
              name="gpa"
              value={studentData.gpa}
              step="0.1"
              min="0"
              max="4"
              onChange={(e) => handleChange(e)}
            />
            {errors.gpa && <p className={classes.errorMessage}>{errors.gpa}</p>}
            <br />
            <br />

            {/* Campus ID */}
            <label style={{ color: "#11153e", fontWeight: "bold" }}>Campus ID: </label>
            <input
              type="text"
              name="campusId"
              value={studentData.campusId || ""}
              onChange={(e) => handleChange(e)}
              disabled={studentData.campusId !== null} // Disable if pre-filled
            />
            <br />
            <br />

            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
            <br />
            <br />
          </form>
        </div>
      </div>
    </div>
  );
};

export default NewStudentView;
