import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

// Create styling for the input form
const useStyles = makeStyles(() => ({
  formContainer: {
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
    padding: '20px',
  },
  title: {
    textAlign: 'center',
    fontFamily: 'Courier, sans-serif',
    fontSize: '20px',
    color: '#11153e',
  },
  inputField: {
    width: '100%',
    height: '40px',
    marginBottom: '20px',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  submitButton: {
    width: '100%',
    height: '40px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    padding: '10px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
}));

const NewStudentView = () => {
  const classes = useStyles();
  const [studentData, setStudentData] = useState({
    firstname: '',
    lastname: '',
    email: '',
    gpa: '',
    campusId: '',
  });

  const handleChange = (event) => {
    setStudentData({ ...studentData, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(studentData);
    // Add your logic to submit the form data here
  };

  return (
    <div className={classes.formContainer}>
      <Typography className={classes.title}>Add New Student</Typography>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstname"
          value={studentData.firstname}
          onChange={handleChange}
          placeholder="First Name"
          className={classes.inputField}
        />
        <br />
        <input
          type="text"
          name="lastname"
          value={studentData.lastname}
          onChange={handleChange}
          placeholder="Last Name"
          className={classes.inputField}
        />
        <br />
        <input
          type="email"
          name="email"
          value={studentData.email}
          onChange={handleChange}
          placeholder="Email"
          className={classes.inputField}
        />
        <br />
        <input
          type="number"
          name="gpa"
          value={studentData.gpa}
          onChange={handleChange}
          placeholder="GPA"
          className={classes.inputField}
        />
        <br />
        <input
          type="text"
          name="campusId"
          value={studentData.campusId}
          onChange={handleChange}
          placeholder="Campus ID"
          className={classes.inputField}
        />
        <br />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.submitButton}
        >
          Add Student
        </Button>
      </form>
    </div>
  );
};

export default NewStudentView;
