/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */



import { useState } from "react";
import { Link } from "react-router-dom";

const CampusView = ({ campus, deleteCampus, createStudent, updateStudent, fetchCampus, allStudents }) => {
  const [newStudent, setNewStudent] = useState({
    firstname: "",
    lastname: "",
    email: "",
    gpa: "",
    campusId: campus.id, // Automatically associate with this campus
  });
  const [selectedStudent, setSelectedStudent] = useState(""); // For existing student dropdown
  const [showNewStudentForm, setShowNewStudentForm] = useState(false);
  const [showExistingStudentDropdown, setShowExistingStudentDropdown] = useState(false);

  // Handle New Student Form Submission
  const handleNewStudentSubmit = async (e) => {
    e.preventDefault();
    await createStudent(newStudent); // Dispatch action to create the new student
    fetchCampus(campus.id); // Refresh campus data
    setNewStudent({ firstname: "", lastname: "", email: "", gpa: "", campusId: campus.id }); // Reset form
    setShowNewStudentForm(false); // Hide the form
  };

  // Handle Adding Existing Student
  const handleAddExistingStudent = async (e) => {
    e.preventDefault();
    if (selectedStudent) {
      await updateStudent(selectedStudent, { campusId: campus.id }); // Dispatch action to update the student
      fetchCampus(campus.id); // Refresh campus data
      setSelectedStudent(""); // Reset dropdown
      setShowExistingStudentDropdown(false); // Hide the dropdown
    }
  };

  // Filter Unregistered Students for the Dropdown
  const unregisteredStudents = allStudents.filter((student) => !student.campusId);

  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>
      <img
        src={campus.imageUrl || "https://via.placeholder.com/150"}
        alt={`${campus.name} campus`}
        style={{ width: "300px", height: "auto" }}
      />
      <button onClick={() => deleteCampus(campus.id)}>Delete Campus</button>

      <h2>Enrolled Students:</h2>
      {campus.students.length ? (
        campus.students.map((student) => (
          <div key={student.id}>
            <Link to={`/student/${student.id}`}>
              <h3>{`${student.firstname} ${student.lastname}`}</h3>
            </Link>
          </div>
        ))
      ) : (
        <p>No students are currently enrolled at this campus.</p>
      )}

      {/* Add New Student */}
      <button onClick={() => setShowNewStudentForm(!showNewStudentForm)}>
        {showNewStudentForm ? "Cancel" : "Add New Student"}
      </button>
      {showNewStudentForm && (
        <form onSubmit={handleNewStudentSubmit}>
          <h3>Add New Student</h3>
          <input
            type="text"
            placeholder="First Name"
            value={newStudent.firstname}
            onChange={(e) => setNewStudent({ ...newStudent, firstname: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Last Name"
            value={newStudent.lastname}
            onChange={(e) => setNewStudent({ ...newStudent, lastname: e.target.value })}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={newStudent.email}
            onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="GPA"
            value={newStudent.gpa}
            onChange={(e) => setNewStudent({ ...newStudent, gpa: e.target.value })}
          />
          <button type="submit">Add Student</button>
        </form>
      )}

      {/* Add Existing Student */}
      <button onClick={() => setShowExistingStudentDropdown(!showExistingStudentDropdown)}>
        {showExistingStudentDropdown ? "Cancel" : "Add Existing Student"}
      </button>
      {showExistingStudentDropdown && (
        <form onSubmit={handleAddExistingStudent}>
          <h3>Add Existing Student</h3>
          <select
            value={selectedStudent}
            onChange={(e) => setSelectedStudent(e.target.value)}
            required
          >
            <option value="">-- Select Student --</option>
            {unregisteredStudents.map((student) => (
              <option key={student.id} value={student.id}>
                {`${student.firstname} ${student.lastname}`}
              </option>
            ))}
          </select>
          <button type="submit">Add to Campus</button>
        </form>
      )}
    </div>
  );
};

export default CampusView;
