/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
// StudentView.js




import { Link } from "react-router-dom";

// Take in props data to construct the component
const StudentView = (props) => {
  const { student, campus, deleteStudent } = props;

  // Render a single Student view with their campus information
  return (
    <div>
      <h1>{`${student.firstname} ${student.lastname}`}</h1>
      <p><strong>Email:</strong> {student.email}</p>
      <p><strong>GPA:</strong> {student.gpa || "N/A"}</p>
      <img
        src={student.imageUrl || "https://via.placeholder.com/150"}
        alt={`${student.firstname} ${student.lastname}`}
        style={{ width: "300px", height: "auto" }}
      />
      <br />
      <button onClick={() => deleteStudent(student.id)}>Delete Student</button>

      <h2>Enrolled Campus:</h2>
      {campus ? (
        <div>
          <Link to={`/campus/${campus.id}`}>
            <h3>{campus.name}</h3>
          </Link>
          <p>{campus.address}</p>
          <p>{campus.description}</p>
        </div>
      ) : (
        <p>Student not enrolled in any campus.</p>
      )}

      <br />
      <Link to={`/add-campus?studentId=${student.id}`}>
        <button>Add Campus</button>
      </Link>

      <br />
      <Link to={`/editstudent/${student.id}`}>
        <button>Edit Student</button>
      </Link>
    </div>
  );
};

export default StudentView;
