import axios from 'axios';
import React, { useState, useEffect } from 'react';

function AddStudentPage() {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({
    id: '',
    firstname: '',
    lastname: '',
    email: '',
    imageurl: ''
  });

  useEffect(() => {
    axios.get('/api/students')
      .then(response => {
        setStudents(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleAddStudent = () => {
    axios.post('/api/students', newStudent)
      .then(response => {
        setStudents([...students, response.data]);
        setNewStudent({
          id: '',
          firstname: '',
          lastname: '',
          email: '',
          imageurl: ''
        });
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewStudent({ ...newStudent, [name]: value });
  };

  return (
    <div>
      <h1>Add Student</h1>
      <form>
        <label>First Name:</label>
        <input type="text" name="firstname" value={newStudent.firstname} onChange={handleInputChange} />
        <br />
        <label>Last Name:</label>
        <input type="text" name="lastname" value={newStudent.lastname} onChange={handleInputChange} />
        <br />
        <label>Email:</label>
        <input type="email" name="email" value={newStudent.email} onChange={handleInputChange} />
        <br />
        <label>Image URL:</label>
        <input type="text" name="imageurl" value={newStudent.imageurl} onChange={handleInputChange} />
        <br />
        <button type="button" onClick={handleAddStudent}>Add Student</button>
      </form>
      <h2>Students:</h2>
      <ul>
        {students.map((student) => (
          <li key={student.id}>
            {student.firstname} {student.lastname} ({student.email})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AddStudentPage;
