/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */

import { useState } from "react";
import { useNavigate } from "react-router-dom";

const EditCampus = ({ campus, updateCampus }) => {
  const [formData, setFormData] = useState({
    name: campus.name,
    address: campus.address,
    description: campus.description,
    imageUrl: campus.imageUrl,
  });

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();
    updateCampus(campus.id, formData); // Assume this is a function passed as a prop
    navigate(`/campus/${campus.id}`); // Redirect to the campus details page after saving
  };

  const handleCancel = () => {
    navigate(`/campus/${campus.id}`); // Redirect back to campus details without saving
  };

  return (
    <div>
      <h1>Edit Campus</h1>
      <form onSubmit={handleSaveChanges}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Address:
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Description:
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
          ></textarea>
        </label>
        <br />
        <label>
          Image URL:
          <input
            type="text"
            name="imageUrl"
            value={formData.imageUrl}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <button type="submit">Save Changes</button>
        <button type="button" onClick={handleCancel}>
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditCampus;
