/*==================================================
NewStudentView.js

The Views component is responsible for rendering the web page with data provided by the corresponding Container component.
It constructs a React component to display the new student page.
================================================== */
import React from 'react';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

// Create styling for the input form
const useStyles = makeStyles(() => ({
  formContainer: {
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
    padding: '20px',
  },
  title: {
    textAlign: 'center',
    fontFamily: 'Courier, sans-serif',
    fontSize: '20px',
    color: '#11153e',
  },
  errorMessage: {
    color: 'red',
    fontSize: '0.9em',
    margin: '5px 0',
  },
  inputField: {
    width: '100%',
    marginBottom: '15px',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
    fontSize: '14px',
  },
  label: {
    fontWeight: 'bold',
    color: '#11153e',
  },
}));

const NewStudentView = (props) => {
  const {
    handleChange,
    handleSubmit,
    studentData = {
      firstname: "",
      lastname: "",
      email: "",
      gpa: "",
      imageUrl: "",
      campusId: "",
      errors: {},
    },
  } = props;

  const classes = useStyles();

  return (
    <div>
      <Typography className={classes.title}>Add a New Student</Typography>
      <form onSubmit={(e) => handleSubmit(e)} className={classes.formContainer}>
        {/* First Name */}
        <div>
          <label className={classes.label}>First Name:</label>
          <input
            type="text"
            name="firstname"
            placeholder="Enter first name"
            value={studentData.firstname}
            onChange={handleChange}
            className={classes.inputField}
          />
          {studentData.errors.firstname && (
            <p className={classes.errorMessage}>{studentData.errors.firstname}</p>
          )}
        </div>

        {/* Last Name */}
        <div>
          <label className={classes.label}>Last Name:</label>
          <input
            type="text"
            name="lastname"
            placeholder="Enter last name"
            value={studentData.lastname}
            onChange={handleChange}
            className={classes.inputField}
          />
          {studentData.errors.lastname && (
            <p className={classes.errorMessage}>{studentData.errors.lastname}</p>
          )}
        </div>

        {/* Email */}
        <div>
          <label className={classes.label}>Email:</label>
          <input
            type="email"
            name="email"
            placeholder="Enter email"
            value={studentData.email}
            onChange={handleChange}
            className={classes.inputField}
          />
          {studentData.errors.email && (
            <p className={classes.errorMessage}>{studentData.errors.email}</p>
          )}
        </div>

        {/* GPA */}
        <div>
          <label className={classes.label}>GPA:</label>
          <input
            type="number"
            name="gpa"
            placeholder="Enter GPA (0.0 - 4.0)"
            value={studentData.gpa}
            step="0.1"
            min="0"
            max="4"
            onChange={handleChange}
            className={classes.inputField}
          />
          {studentData.errors.gpa && (
            <p className={classes.errorMessage}>{studentData.errors.gpa}</p>
          )}
        </div>

        {/* Image URL */}
        <div>
          <label className={classes.label}>Image URL (Optional):</label>
          <input
            type="text"
            name="imageUrl"
            placeholder="Enter image URL (optional)"
            value={studentData.imageUrl}
            onChange={handleChange}
            className={classes.inputField}
          />
          {studentData.errors.imageUrl && (
            <p className={classes.errorMessage}>{studentData.errors.imageUrl}</p>
          )}
        </div>

        {/* Campus ID */}
        <div>
          <label className={classes.label}>Campus ID (Optional):</label>
          <input
            type="text"
            name="campusId"
            placeholder="Enter campus ID (optional)"
            value={studentData.campusId}
            onChange={handleChange}
            className={classes.inputField}
          />
        </div>

        {/* Submit Button */}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          style={{ marginTop: "20px", width: "100%" }}
        >
          Submit
        </Button>
      </form>
    </div>
  );
};

export default NewStudentView;
