/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */
import { Link } from "react-router-dom";

// Take in props data to construct the component
const CampusView = (props) => {
  const {campus, deleteStudent } = props;

  // Render a single Campus view with list of its students
  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>
      <img src={campus.imageUrl || "https://via.placeholder.com/150"} alt={`${campus.name} campus`} style={{ width: "300px", height: "auto" }} />
      <br></br>

      <h2>Enrolled Students:</h2>
      {campus.students && campus.students.length > 0 ? (
        campus.students.map((student) => {
          let name = `${student.firstname} ${student.lastname}`;
          return (
            <div key={student.id}>
              <Link to={`/student/${student.id}`}>
                <h3>{name}</h3>
              </Link>
              <button onClick={() => deleteStudent(student.id)}>Remove Student</button>
            </div>
          );
        })
      ) : (
        <p>No students are currently enrolled at this campus.</p>
      )}

      <br></br>

      <Link to="/newstudent">
        <button>Add New Student</button>
      </Link>

      <br></br>
      <Link to={`/editcampus/${campus.id}`}>
        <button>Edit Campus</button>
      </Link>
    </div>
  );
};

export default CampusView;
