// Import Material-UI components
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";

import { Link } from "react-router-dom";

// Define styling for the header
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
    textAlign: "left",
    fontWeight: 700,
    fontFamily: "Arial, sans-serif",
    fontSize: "30px",
    color: "white", // White text for contrast
    textShadow: "1px 1px 3px rgba(0, 0, 0, 0.8)", // Subtle shadow for readability
  },
  appBar: {
    backgroundImage:
      "url('https://media.cntraveler.com/photos/631b4fe1f2f54501e692c5d3/16:9/w_2991,h_1682,c_limit/University%20of%20Michigan_GettyImages-470656298.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    boxShadow: "none",
    height: "250px", // Adjust height as needed
    position: "relative", // Required for overlay positioning
  },
  overlay: {
    backgroundColor: "rgba(0, 0, 0, 0.3)", // Softer overlay (reduced opacity)
    height: "100%",
    width: "100%",
    position: "absolute",
    top: 0,
    left: 0,
  },
  content: {
    position: "relative", // Ensures content appears above the overlay
    zIndex: 2,
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    height: "100%",
    padding: "0 20px",
  },
  links: {
    textDecoration: "none",
  },
  button: {
    backgroundColor: "rgba(255, 255, 255, 0.9)", // Semi-transparent white
    color: "#333333", // Muted dark gray text
    fontWeight: 600,
    margin: "0 8px",
    padding: "8px 20px",
    borderRadius: "20px",
    textTransform: "none",
    transition: "all 0.3s ease",
    "&:hover": {
      backgroundColor: "rgba(255, 255, 255, 1)", // Fully opaque on hover
      color: "#555555", // Slightly darker text on hover
    },
  },
}));

const Header = () => {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        {/* Overlay for soft darkening */}
        <div className={classes.overlay}></div>
        <div className={classes.content}>
          <Typography variant="h6" className={classes.title}>
            Campus Management System
          </Typography>

          <div>
            <Link className={classes.links} to={"/"}>
              <Button className={classes.button}>Home</Button>
            </Link>

            <Link className={classes.links} to={"/campuses"}>
              <Button className={classes.button}>All Campuses</Button>
            </Link>

            <Link className={classes.links} to={"/students"}>
              <Button className={classes.button}>All Students</Button>
            </Link>
          </div>
        </div>
      </AppBar>
    </div>
  );
};

export default Header;
