import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { addCampusThunk } from "../../store/thunks";
import AddCampusView from "../views/AddCampusView";

class AddCampusContainer extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      address: "",
      description: "",
      imageUrl: "",
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    await this.props.addCampus(this.state); // Dispatch the thunk to add a campus
    this.setState({
      name: "",
      address: "",
      description: "",
      imageUrl: "",
    });
    this.props.history.push("/campuses"); // Redirect to the campuses page
  };

  render() {
    return (
      <AddCampusView
        handleSubmit={this.handleSubmit}
        handleChange={this.handleChange}
        name={this.state.name}
        address={this.state.address}
        description={this.state.description}
        imageUrl={this.state.imageUrl}
      />
    );
  }
}

const mapDispatch = (dispatch) => {
  return {
    addCampus: (campus) => dispatch(addCampusThunk(campus)),
  };
};

AddCampusContainer.propTypes = {
  addCampus: PropTypes.func.isRequired,
};

export default connect(null, mapDispatch)(AddCampusContainer);
