// EditStudentView.js
import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import axios from "axios";

const EditStudentView = () => {
  const { id } = useParams();  // Get student ID from URL
  const [student, setStudent] = useState({});
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const history = useHistory();  // Used for redirecting after success

  // Fetch the student's data when the component is mounted
  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const response = await axios.get(`/api/students/${id}`);
        setStudent(response.data);
      } catch (error) {
        console.error("Error fetching student data:", error);
        setError("An error occurred while fetching the student data.");
      }
    };

    fetchStudent();
  }, [id]);

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(`/api/students/${id}`, student);
      setSuccess("Student updated successfully!");

      // Redirect to the student view page after success
      history.push(`/student/${id}`);
    } catch (error) {
      console.error("Error updating student:", error);
      setError("An error occurred while updating the student.");
    }
  };

  return (
    <div>
      <h1>Edit Student: {student.firstname} {student.lastname}</h1>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>First Name:</label>
          <input
            type="text"
            name="firstname"
            value={student.firstname || ""}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Last Name:</label>
          <input
            type="text"
            name="lastname"
            value={student.lastname || ""}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={student.email || ""}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Image URL:</label>
          <input
            type="text"
            name="imageurl"
            value={student.imageurl || ""}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>GPA:</label>
          <input
            type="number"
            name="gpa"
            value={student.gpa || ""}
            onChange={handleChange}
            min="0"
            max="4"
            step="0.1"
          />
        </div>
        <div>
          <label>Campus ID:</label>
          <input
            type="number"
            name="campusId"
            value={student.campusId || ""}
            onChange={handleChange}
          />
        </div>

        <button type="submit">Save Changes</button>
      </form>

      <Link to={`/student/${id}`}>
        <button>Cancel</button>
      </Link>
    </div>
  );
};

export default EditStudentView;
