// Import Material-UI components
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";

import { Link } from "react-router-dom";

// Define styling for the header
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
    textAlign: "left",
    fontWeight: 600,
    fontFamily: "Arial, sans-serif",
    fontSize: "28px",
    color: "#333333", // Muted dark gray for text
  },
  appBar: {
    background: "linear-gradient(90deg, #dfe4ea, #a4b0be)", // Muted gradient
    boxShadow: "none",
  },
  links: {
    textDecoration: "none",
  },
  button: {
    backgroundColor: "#f0f0f0", // Soft gray background
    color: "#333333", // Muted dark gray text
    fontWeight: 500,
    margin: "0 8px",
    padding: "6px 18px",
    borderRadius: "15px",
    textTransform: "none",
    transition: "all 0.3s ease",
    "&:hover": {
      backgroundColor: "#dcdde1", // Slightly darker on hover
      color: "#555555", // Slightly darker text on hover
    },
  },
}));

const Header = () => {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            Campus Management System
          </Typography>

          <Link className={classes.links} to={"/"}>
            <Button className={classes.button}>Home</Button>
          </Link>

          <Link className={classes.links} to={"/campuses"}>
            <Button className={classes.button}>All Campuses</Button>
          </Link>

          <Link className={classes.links} to={"/students"}>
            <Button className={classes.button}>All Students</Button>
          </Link>
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Header;
