import React, { useState } from "react";
import { useLocation } from "react-router-dom";

const AddStudentForm = ({ submitStudent, history }) => {
  // Extract campusId from the URL query parameters
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const campusId = params.get("campusId");

  // State for the form fields
  const [formData, setFormData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    imageUrl: "",
    gpa: "",
    campusId: campusId || "", // Prefill campusId if available
  });

  // Handle form field changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate required fields
    if (!formData.firstname || !formData.lastname || !formData.email || !formData.campusId) {
      alert("Please fill in all required fields.");
      return;
    }

    // Submit the form data
    submitStudent(formData);
    history.push("/students"); // Redirect to students list after submission
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add a Student</h2>
      <label>
        First Name:
        <input
          type="text"
          name="firstname"
          value={formData.firstname}
          onChange={handleChange}
          required
        />
      </label>
      <br />
      <label>
        Last Name:
        <input
          type="text"
          name="lastname"
          value={formData.lastname}
          onChange={handleChange}
          required
        />
      </label>
      <br />
      <label>
        Email:
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
      </label>
      <br />
      <label>
        Image URL:
        <input
          type="text"
          name="imageUrl"
          value={formData.imageUrl}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        GPA:
        <input
          type="number"
          name="gpa"
          value={formData.gpa}
          onChange={handleChange}
          step="0.1"
          min="0"
          max="4"
        />
      </label>
      <br />
      <label>
        Campus ID:
        <input
          type="text"
          name="campusId"
          value={formData.campusId} // Prefill campusId if available
          onChange={handleChange}
          required
        />
      </label>
      <br />
      <button type="submit">Submit</button>
    </form>
  );
};

export default AddStudentForm;
