/*==================================================
NewStudentView.js

The Views component is responsible for rendering the web page with data provided by the corresponding Container component.
It constructs a React component to display the new student page.
================================================== */
import React from 'react';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

// Create styling for the input form
const useStyles = makeStyles(() => ({
  formContainer: {
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
    padding: '20px',
  },
  title: {
    textAlign: 'center',
    fontFamily: 'Courier, sans-serif',
    fontSize: '20px',
    color: '#11153e',
  },
  errorMessage: {
    color: 'red',
    fontSize: '0.8em',
    marginBottom: '10px',
  },
  inputField: {
    width: '100%',
    height: '40px',
    marginBottom: '20px',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  submitButton: {
    width: '100%',
    height: '40px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    padding: '10px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
}));

const NewStudentView = ({ handleChange, handleSubmit, studentData }) => {
  const classes = useStyles();

  // Check if the form is valid
  const isFormValid = () => {
    return (
      studentData.firstname &&
      studentData.lastname &&
      studentData.email &&
      studentData.gpa !== undefined && // Ensure GPA is provided
      studentData.campusId !== undefined && // Ensure Campus ID is provided
      studentData.imageUrl
    );
  };

  return (
    <div className={classes.formContainer}>
      <Typography className={classes.title}>Add New Student</Typography>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstname"
          value={studentData.firstname}
          onChange={handleChange}
          placeholder="First Name"
          className={classes.inputField}
          required
        />
        {studentData.errors?.firstname && (
          <div className={classes.errorMessage}>
            {studentData.errors.firstname}
          </div>
        )}
        <input
          type="text"
          name="lastname"
          value={studentData.lastname}
          onChange={handleChange}
          placeholder="Last Name"
          className={classes.inputField}
          required
        />
        {studentData.errors?.lastname && (
          <div className={classes.errorMessage}>
            {studentData.errors.lastname}
          </div>
        )}
        <input
          type="email"
          name="email"
          value={studentData.email}
          onChange={handleChange}
          placeholder="Email"
          className={classes.inputField}
          required
        />
        {studentData.errors?.email && (
          <div className={classes.errorMessage}>
            {studentData.errors.email}
          </div>
        )}
        <input
          type="number"
          name="gpa"
          value={studentData.gpa}
          onChange={handleChange}
          placeholder="GPA"
          className={classes.inputField}
          required
          min="0"
          max="4"
        />
        {studentData.errors?.gpa && (
          <div className={classes.errorMessage}>
            {studentData.errors.gpa}
          </div>)}
        <input
          type="text"
          name="campusId"
          value={studentData.campusId}
          onChange={handleChange}
          placeholder="Campus ID"
          className={classes.inputField}
          required
        />
        {studentData.errors?.campusId && (
          <div className={classes.errorMessage}>
            {studentData.errors.campusId}
          </div>
        )}
        <input
          type="text"
          name="imageUrl"
          value={studentData.imageUrl}
          onChange={handleChange}
          placeholder="Image URL"
          className={classes.inputField}
        />
        {studentData.errors?.imageUrl && (
          <div className={classes.errorMessage}>
            {studentData.errors.imageUrl}
          </div>
        )}
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className={classes.submitButton}
          disabled={!isFormValid()} // Disable button if form is invalid
        >
          Add Student
        </Button>
      </form>
    </div>
  );
};

export default NewStudentView;
