/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { deleteStudent } from '../api';

const StudentView = ({ student, campus }) => {
  const history = useHistory();
  const [studentData, setStudentData] = useState(student);
  const [campusData, setCampusData] = useState(campus);

  useEffect(() => {
    setStudentData(student);
    setCampusData(campus);
  }, [student, campus]);

  const handleDelete = async () => {
    await deleteStudent(studentData.id);
    history.push('/students');
  };

  return (
    <div>
      <h2>Student Information</h2>
      <p>First Name: {studentData.firstname}</p>
      <p>Last Name: {studentData.lastname}</p>
      <p>Email: {studentData.email}</p>
      <p>Phone Number: {studentData.phonenumber}</p>
      <p>Address: {studentData.address}</p>
      {campusData ? (
        <div>
          <p>Campus: {campusData.name}</p>
          <Link to={`/campuses/${campusData.id}`}>
            View Campus Details
          </Link>
        </div>
      ) : (
        <p>Not enrolled at a campus</p>
      )}
      <Link to={`/students/${studentData.id}/edit`}>
        Edit Student Information
      </Link>
      <button onClick={handleDelete}>Delete Student</button>
    </div>
  );
};

export default StudentView;
