// /*==================================================
// StudentView.js

// The Views component is responsible for rendering web page with data provided by the corresponding Container component.
// It constructs a React component to display the single student view page.
// ================================================== */
// StudentView.js

// The Views component is responsible for rendering a web page with data provided by the corresponding Container component.
// It constructs a React component to display the single student view page.
// StudentView.js
// StudentView.js

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const StudentView = ({ student, campus, fetchStudent, onDeleteStudent }) => {
  const [campuses, setCampuses] = useState([]);
  const [selectedCampusId, setSelectedCampusId] = useState("");
  const [isCampusDropdownVisible, setIsCampusDropdownVisible] = useState(false);
  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchCampuses = async () => {
      try {
        const response = await axios.get("/api/campuses");
        setCampuses(response.data);
      } catch (error) {
        console.error("Error fetching campuses:", error);
        setError("Failed to fetch campuses.");
      }
    };
    fetchCampuses();
  }, []);

  const handleRemoveFromCampus = async () => {
    const confirmation = window.confirm(
      "Are you sure you want to remove this student from this campus?"
    );
    if (confirmation) {
      try {
        await axios.put(`/api/students/${student.id}/`, { campusId: null });
        setSuccess("Student removed from campus successfully!");
        fetchStudent(student.id);
      } catch (error) {
        console.error("Error removing student from campus:", error);
        setError("An error occurred while removing the student from campus.");
      }
    }
  };

  const handleAddCampus = () => {
    if (student.campusId) {
      setError("Campus can't be added, student is already enrolled.");
    } else {
      setIsCampusDropdownVisible(true);
    }
  };

  const handleCampusSelect = (event) => {
    setSelectedCampusId(event.target.value);
  };

  const handleAddCampusToStudent = async () => {
    if (!selectedCampusId) {
      setError("Please select a campus.");
      return;
    }

    try {
      await axios.put(`/api/students/${student.id}/`, { campusId: selectedCampusId });
      setSuccess("Student successfully added to the campus!");
      fetchStudent(student.id);
      setIsCampusDropdownVisible(false);
      setSelectedCampusId("");
    } catch (error) {
      console.error("Error adding student to campus:", error);
      setError("An error occurred while adding the student to campus.");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.studentCard}>
        <div style={styles.studentDetails}>
          <h1 style={styles.name}>{`${student.firstname} ${student.lastname}`}</h1>
          <p style={styles.detail}>Email: {student.email}</p>
          <p style={styles.detail}>GPA: {student.gpa || "N/A"}</p>
        </div>
        <img
          src={student.imageurl || "https://via.placeholder.com/150"}
          alt={`${student.firstname} ${student.lastname}`}
          style={styles.studentImage}
        />
      </div>

      <div style={styles.sectionBox}>
        <h2 style={styles.sectionTitle}>Enrolled Campus:</h2>
        {student.campus ? (
          <div>
            <Link to={`/campus/${student.campus.id}`} style={styles.link}>
              <h3 style={styles.campusName}>{student.campus.name}</h3>
            </Link>
            <p style={styles.detail}>{student.campus.address}</p>
            <button style={styles.removeButton} onClick={handleRemoveFromCampus}>
              Remove from Campus
            </button>
          </div>
        ) : (
          <p style={styles.noCampus}>Student not enrolled in any campus.</p>
        )}
      </div>

      {success && <p style={styles.successMessage}>{success}</p>}
      {error && <p style={styles.errorMessage}>{error}</p>}

      <div style={styles.buttonContainer}>
        <button style={styles.addButton} onClick={handleAddCampus}>
          Add Campus
        </button>
        <Link to={`/editstudent/${student.id}`}>
          <button style={styles.editButton}>Edit Student</button>
        </Link>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: "800px",
    margin: "auto",
    padding: "20px",
    backgroundColor: "#f4f4f4", // Subtle light gray background
    borderRadius: "12px",
    boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
  },
  studentCard: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: "20px",
    borderRadius: "12px",
    backgroundColor: "#ffffff",
    marginBottom: "20px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.05)",
  },
  studentDetails: {
    flex: 1,
    textAlign: "left",
  },
  name: {
    fontSize: "26px",
    fontWeight: "bold",
    color: "#333333",
    marginBottom: "10px",
  },
  detail: {
    fontSize: "16px",
    color: "#555555",
    marginBottom: "5px",
  },
  studentImage: {
    width: "120px",
    height: "120px",
    borderRadius: "50%",
    objectFit: "cover",
    border: "2px solid #ddd",
  },
  sectionBox: {
    padding: "20px",
    borderRadius: "12px",
    backgroundColor: "#ffffff",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.05)",
  },
  sectionTitle: {
    fontSize: "20px",
    fontWeight: "bold",
    marginBottom: "15px",
    color: "#333333",
  },
  campusName: {
    fontSize: "18px",
    color: "#444444", // Neutral dark gray
    marginBottom: "5px",
  },
  link: {
    textDecoration: "none",
  },
  removeButton: {
    backgroundColor: "#e0e0e0", // Light gray
    color: "#333333",
    border: "none",
    borderRadius: "8px",
    padding: "10px 15px",
    fontSize: "14px",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  addButton: {
    backgroundColor: "#6c757d", // Neutral gray
    color: "#ffffff",
    border: "none",
    borderRadius: "8px",
    padding: "10px 20px",
    fontSize: "14px",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  editButton: {
    backgroundColor: "#6fa8dc", // Soft blue
    color: "#ffffff",
    border: "none",
    borderRadius: "8px",
    padding: "10px 20px",
    fontSize: "14px",
    marginLeft: "10px",
    cursor: "pointer",
    transition: "all 0.3s ease",
  },
  buttonContainer: {
    textAlign: "center",
    marginTop: "20px",
  },
  successMessage: {
    color: "green",
    textAlign: "center",
    marginBottom: "20px",
  },
  errorMessage: {
    color: "red",
    textAlign: "center",
    marginBottom: "20px",
  },
};

export default StudentView;
