import { useState } from "react";
import { Link } from "react-router-dom";
import { addStudentThunk } from "../../store/thunks";
import NewStudentView from "./NewStudentView"; // Import the NewStudentView component

const CampusView = (props) => {
  const {
    campus,
    allStudents,
    updateStudent,
    removeStudentFromCampus,
    fetchCampus,
  } = props;

  const [showNewStudentForm, setShowNewStudentForm] = useState(false);

  // New student state
  const [newStudent, setNewStudent] = useState({
    firstname: "",
    lastname: "",
    email: "",
    gpa: "",
    campusId: campus.id, // Pre-select the campus ID
    campusName: campus.name, // Pre-fill campus name for display
  });

  const [errors, setErrors] = useState({});
  const [blankFieldError, setBlankFieldError] = useState("");

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewStudent({ ...newStudent, [name]: value });
    setErrors({ ...errors, [name]: "" }); // Clear error for the field
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validate required fields
    if (!newStudent.firstname || !newStudent.lastname || !newStudent.email || !newStudent.campusId) {
      setBlankFieldError("Please fill out all required fields.");
      return;
    }
    setBlankFieldError("");

    try {
      const addedStudent = await addStudentThunk(newStudent); // Call the API to add the student
      if (addedStudent) {
        setNewStudent({
          firstname: "",
          lastname: "",
          email: "",
          gpa: "",
          campusId: campus.id,
          campusName: campus.name,
        });
        setShowNewStudentForm(false);
        await fetchCampus(campus.id); // Re-fetch updated campus data
      }
    } catch (error) {
      console.error("Failed to add student:", error);
    }
  };

  // Handle form cancellation
  const handleCancel = () => {
    setShowNewStudentForm(false);
    setNewStudent({
      firstname: "",
      lastname: "",
      email: "",
      gpa: "",
      campusId: campus.id,
      campusName: campus.name,
    });
    setErrors({});
    setBlankFieldError("");
  };

  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>
      <img
        src={campus.imageUrl || "https://via.placeholder.com/150"}
        alt={`${campus.name} campus`}
        style={{ width: "300px", height: "auto" }}
      />
      <br />

      <h2>Enrolled Students:</h2>
      {campus.students && campus.students.length > 0 ? (
        campus.students.map((student) => (
          <div key={student.id}>
            <Link to={`/student/${student.id}`}>
              <h3>{`${student.firstname} ${student.lastname}`}</h3>
            </Link>
            <button onClick={() => handleRemoveStudent(student.id)}>
              Remove Student
            </button>
          </div>
        ))
      ) : (
        <p>No students are currently enrolled at this campus.</p>
      )}

      <br />

      {/* Add New Student Button */}
      <button onClick={() => setShowNewStudentForm(!showNewStudentForm)}>
        {showNewStudentForm ? "Cancel Add New Student" : "Add New Student"}
      </button>

      {/* New Student Form */}
      {showNewStudentForm && (
        <NewStudentView
          newStudent={newStudent}
          errors={errors}
          blankFieldError={blankFieldError}
          handleChange={handleChange}
          handleSubmit={handleSubmit}
          handleCancel={handleCancel}
        />
      )}

      <br />
      <Link to={`/editcampus/${campus.id}`}>
        <button>Edit Campus</button>
      </Link>
    </div>
  );
};

export default CampusView;
