/*==================================================
NewStudentContainer.js

The Container component is responsible for stateful logic and data fetching, and
passes data (if any) as props to the corresponding View component.
If needed, it also defines the component's "connect" function.
================================================== */
import Header from './Header';
import { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

import NewStudentView from '../views/NewStudentView';
import { addStudentThunk } from '../../store/thunks';

class NewStudentContainer extends Component {
  // Initialize state
  constructor(props) {
    super(props);
    this.state = {
      firstname: "",
      lastname: "",
      email: "",
      gpa: "",
      campusId: null,
      redirect: false,
      redirectId: null,
    };
  }

  // Capture input data when it is entered
  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  // Take action after user clicks the submit button
  handleSubmit = async (event) => {
    event.preventDefault(); // Prevent browser reload/refresh after submit.

    const { firstname, lastname, email, gpa, imageUrl, campusId } = this.state;

    let errors = {};

    // Validate required fields
    if (!firstname) errors.firstname = "First name is required.";
    if (!lastname) errors.lastname = "Last name is required.";
    if (!email) errors.email = "Email is required.";
    if (gpa && (gpa < 0.0 || gpa > 4.0)) errors.gpa = "GPA must be between 0.0 and 4.0.";

    // Check if there are errors
    if (Object.keys(errors).length > 0) {
      this.setState({ errors });
      return;
    }

    // Prepare student object
    let student = {
      firstname,
      lastname,
      email,
      imageUrl: imageUrl || "https://via.placeholder.com/150", // Default image URL
      gpa: gpa ? parseFloat(gpa) : null, // Ensure GPA is stored as a number
      campusId: campusId || null, // Allow null if no campusId is provided
    };

    try {
      // Add new student to the back-end database
      let newStudent = await this.props.addStudent(student);

      // Update state, and trigger redirect to show the new student
      if (newStudent) {
        this.setState({
          firstname: "",
          lastname: "",
          email: "",
          gpa: "",
          imageUrl: "",
          campusId: null,
          errors: {}, // Clear errors
          redirect: true,
          redirectId: newStudent.id,
        });
      }
    } catch (err) {
      console.error("Error adding student:", err);
      alert("Failed to add student. Please try again.");
    }
  };

  handleChange = (event) => {
    const { name, value } = event.target;

    // Clear error for the field being updated
    this.setState((prevState) => ({
      [name]: value,
      errors: {
        ...prevState.errors,
        [name]: null,
      },
    }));
  };

  // Unmount when the component is being removed from the DOM:
  componentWillUnmount() {
    this.setState({ redirect: false, redirectId: null });
  }

  // Render new student input form
  render() {
    // Redirect to new student's page after submit
    if (this.state.redirect) {
      return <Redirect to={`/students/${this.state.redirectId}`} />;
    }

    // Display the input form via the corresponding View component
    return (
      <div>
        <Header />
        <NewStudentView
        handleChange={this.handleChange}
        handleSubmit={this.handleSubmit}
        studentData={{
        firstname: this.state.firstname || "", // Ensure it's initialized
        lastname: this.state.lastname || "", // Ensure it's initialized
        email: this.state.email || "", // Ensure it's initialized
        gpa: this.state.gpa || "", // Ensure it's initialized
        campusId: this.state.campusId || "", // Ensure it's initialized
        imageUrl: this.state.imageUrl || "", // Include imageUrl
        errors: this.state.errors || {}, // Ensure it's initialized
  }}
/>
      </div>
    );
  }
}

// The following input argument is passed to the "connect" function used by "NewStudentContainer" component to connect to Redux Store.
// The "mapDispatch" argument is used to dispatch Action (Redux Thunk) to Redux Store.
const mapDispatch = (dispatch) => {
  return {
    addStudent: (student) => dispatch(addStudentThunk(student)),
  };
};

// Export store-connected container by default
// NewStudentContainer uses "connect" function to connect to Redux Store and to read values from the Store
// (and re-read the values when the Store State updates).
export default connect(null, mapDispatch)(NewStudentContainer);
