import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams, withRouter } from 'react-router-dom';
import StudentView from '../views/StudentView';
import { 
  fetchStudentThunk, 
  fetchCampusThunk,
  deleteStudentThunk 
} from "../../store/thunks";;

const StudentContainer = ({
  student, 
  campus, 
  fetchStudent, 
  fetchCampus,
  deleteStudent,
  history // From React Router v5
}) => {
  const { studentId } = useParams();

  useEffect(() => {
    fetchStudent(studentId);
  }, [studentId, fetchStudent]);

  useEffect(() => {
    if (student && student.campusId) {
      fetchCampus(student.campusId);
    }
  }, [student, fetchCampus]);

  const handleDeleteStudent = (id) => {
    deleteStudent(id);
    // Use history for navigation
    history.push('/students');
  };

  return (
    <StudentView 
      student={student} 
      campus={campus}
      onDeleteStudent={handleDeleteStudent}
    />
  );
};

const mapStateToProps = (state) => ({
  student: state.student?.selectedStudent || null,
    campus: state.campus?.selectedCampus || null
});

const mapDispatchToProps = (dispatch) => ({
  fetchStudent: (id) => dispatch(fetchStudentThunk(id)),
  fetchCampus: (id) => dispatch(fetchCampusThunk(id)),
  deleteStudent: (id) => dispatch(deleteStudentThunk(id))
});

// Wrap with withRouter for older React Router versions
export default connect(mapStateToProps, mapDispatchToProps)(withRouter(StudentContainer));
