import Header from './Header';
import { Component } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import NewStudentView from '../views/NewStudentView';
import { addStudentThunk } from '../../store/thunks';

class NewStudentContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: "",
      lastname: "",
      email: "",
      gpa: "",
      campusId: null,
      imageUrl: "",
      redirect: false,
      redirectId: null,
      errors: {},
    };
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
      errors: {
        ...this.state.errors,
        [name]: null, // Clear error for the field being updated
      },
    });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { firstname, lastname, email, gpa, imageUrl, campusId } = this.state;

    // Validate required fields
    let errors = {};
    if (!firstname) errors.firstname = "First name is required.";
    if (!lastname) errors.lastname = "Last name is required.";
    if (!email) errors.email = "Email is required.";
    if (!gpa) errors.gpa = "GPA is required.";
    if (gpa && (gpa < 0.0 || gpa > 4.0)) {
      errors.gpa = "GPA must be between 0.0 and 4.0.";
    }

    if (Object.keys(errors).length > 0) {
      this.setState({ errors });
      return;
    }

    // Prepare the student object
    let student = {
      firstname,
      lastname,
      email,
      gpa: parseFloat(gpa),
      imageUrl: imageUrl || "https://via.placeholder.com/150",
      campusId: campusId || null,
    };

    try {
      const newStudent = await this.props.addStudent(student);
      if (newStudent) {
        this.setState({
          firstname: "",
          lastname: "",
          email: "",
          gpa: "",
          imageUrl: "",
          campusId: null,
          errors: {},
          redirect: true,
          redirectId: newStudent.id,
        });
      }
    } catch (err) {
      console.error("Error adding student:", err);
      alert("Failed to add student. Please try again.");
    }
  };

  componentWillUnmount() {
    this.setState({ redirect: false, redirectId: null });
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={`/students/${this.state.redirectId}`} />;
    }

    return (
      <div>
        <Header />
        <NewStudentView
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
          studentData={{
            firstname: this.state.firstname || "",
            lastname: this.state.lastname || "",
            email: this.state.email || "",
            gpa: this.state.gpa || "",
            campusId: this.state.campusId || "",
            imageUrl: this.state.imageUrl || "",
            errors: this.state.errors || {},
          }}
 />
      </div>
    );
  }
}

const mapDispatch = (dispatch) => {
  return {
    addStudent: (student) => dispatch(addStudentThunk(student)),
  };
};

export default connect(null, mapDispatch)(NewStudentContainer);
