/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
// StudentView.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const StudentView = (props) => {
  const { student } = props;
  const [campuses, setCampuses] = useState([]); // List of all campuses
  const [selectedCampusId, setSelectedCampusId] = useState(""); // Store selected campus ID (initialized as empty string)
  const [isCampusDropdownVisible, setIsCampusDropdownVisible] = useState(false); // Track if the dropdown is visible
  const [isAddStudentFormVisible, setIsAddStudentFormVisible] = useState(false); // Track visibility of "Add Existing Student" form
  const [success, setSuccess] = useState(""); // Track success messages
  const [error, setError] = useState(""); // Track error messages

  // Fetch all campuses when the component is mounted
  useEffect(() => {
    const fetchCampuses = async () => {
      try {
        const response = await axios.get("/api/campuses");
        setCampuses(response.data);
      } catch (error) {
        console.error("Error fetching campuses:", error);
        setError("Failed to fetch campuses.");
      }
    };
    fetchCampuses();
  }, []);

  // Handle showing/hiding the "Add Existing Student" form
  const toggleAddStudentForm = () => {
    setIsAddStudentFormVisible(!isAddStudentFormVisible); // Toggle the form visibility
  };

  return (
    <div>
      <h1>{`${student.firstname} ${student.lastname}`}</h1>
      <p>Email: {student.email}</p>
      <p>GPA: {student.gpa || "N/A"}</p>
      <img
        src={student.imageurl || "https://via.placeholder.com/150"}
        alt={`${student.firstname} ${student.lastname}`}
        style={{ width: "300px", height: "auto" }}
      />
      <br />

      <h2>Enrolled Students:</h2>
      {student.campus ? (
        <div>
          <Link to={`/campus/${student.campus.id}`}>
            <h3>{student.campus.name}</h3>
          </Link>
          <p>{student.campus.address}</p>
        </div>
      ) : (
        <p>Student not enrolled in any campus.</p>
      )}

      <br />
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      <br />

      {/* Toggle "Add Existing Student" Form */}
      <button onClick={toggleAddStudentForm}>
        {isAddStudentFormVisible
          ? "Close Add Existing Student Form"
          : "Add New Student to This Campus"}
      </button>

      {isAddStudentFormVisible && (
        <div>
          <h3>Add Existing Student</h3>
          <select onChange={(e) => setSelectedCampusId(e.target.value)} value={selectedCampusId}>
            <option value="">Select a student</option>
            {campuses.map((campus) => (
              <option key={campus.id} value={campus.id}>
                {campus.name}
              </option>
            ))}
          </select>
          <button>Add to Campus</button>
        </div>
      )}

      <br />
      <Link to={`/editstudent/${student.id}`}>
        <button>Edit Student</button>
      </Link>
    </div>
  );
};

export default StudentView;
