/*==================================================
StudentView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the single student view page.
================================================== */
// StudentView.js
import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import StudentView from '../views/StudentView';
import { 
  fetchSingleStudentThunk, 
  fetchSingleCampusThunk,
  deleteStudentThunk 
} from "../../store/thunks";

const StudentContainer = ({
  student, 
  campus, 
  fetchSingleStudent, 
  fetchSingleCampus,
  deleteStudent
}) => {
  const { studentId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchSingleStudent(studentId);
  }, [studentId, fetchSingleStudent]);

  useEffect(() => {
    if (student && student.campusId) {
      fetchSingleCampus(student.campusId);
    }
  }, [student, fetchSingleCampus]);

  const handleDeleteStudent = (id) => {
    deleteStudent(id);
    navigate('/students'); // Redirect after deletion
  };

  return (
    <StudentView 
      student={student} 
      campus={campus}
      onDeleteStudent={handleDeleteStudent}
    />
  );
};

const mapStateToProps = (state) => ({
  student: state.student.selectedStudent,
  campus: state.campus.selectedCampus
});

const mapDispatchToProps = (dispatch) => ({
  fetchSingleStudent: (id) => dispatch(fetchSingleStudentThunk(id)),
  fetchSingleCampus: (id) => dispatch(fetchSingleCampusThunk(id)),
  deleteStudent: (id) => dispatch(deleteStudentThunk(id))
});

export default connect(mapStateToProps, mapDispatchToProps)(StudentContainer);
