// /*==================================================
// AllStudentsView.js

// The Views component is responsible for rendering web page with data provided by the corresponding Container component.
// It constructs a React component to display the all students view page.
// ================================================== */
import React from 'react';
import { Link } from 'react-router-dom';

const AllStudentsView = (props) => {
  const { students, deleteStudent } = props;

  // Check if no students exist
  if (!students.length) {
    return (
      <div style={styles.container}>
        <h1 style={styles.title}>All Students</h1>
        <div style={styles.noDataMessage}>No students available.</div>
        <br />
        <Link to="/newstudent">
          <button style={styles.addButton}>Add New Student</button>
        </Link>
      </div>
    );
  }

  // Render list of students
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>All Students</h1>
      {students.map((student) => {
        let name = student.firstname + " " + student.lastname;
        return (
          <div key={student.id} style={styles.card}>
            <div style={styles.cardContent}>
              <Link to={`/student/${student.id}`} style={styles.link}>
                <h2 style={styles.studentName}>{name}</h2>
              </Link>
              <button
                style={styles.deleteButton}
                onClick={() => deleteStudent(student.id)}
              >
                Delete
              </button>
            </div>
          </div>
        );
      })}
      <br />
      <Link to="/newstudent">
        <button style={styles.addButton}>Add New Student</button>
      </Link>
    </div>
  );
};

// CSS Styles
const styles = {
  container: {
    padding: "40px",
    backgroundColor: "#2c2c2c", // Dark muted background
    minHeight: "100vh",
  },
  title: {
    fontFamily: "'Merriweather', serif",
    fontWeight: "bold",
    fontSize: "40px",
    color: "#f5f5f5", // Soft white for title
    textAlign: "center",
    marginBottom: "30px",
  },
  card: {
    backgroundColor: "#3b3b3b", // Dark gray for cards
    borderRadius: "12px",
    padding: "20px",
    margin: "20px auto",
    maxWidth: "600px",
    boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.4)", // Softer shadow for elegance
    border: "1px solid #555555", // Subtle border to distinguish cards
  },
  cardContent: {
    textAlign: "left",
  },
  studentName: {
    fontSize: "24px",
    fontWeight: "600",
    color: "#81a1c1", // Muted blue for student names
    margin: "0 0 10px 0",
  },
  link: {
    textDecoration: "none",
  },
  deleteButton: {
    backgroundColor: "#bf616a", // Muted red for delete button
    color: "white",
    border: "none",
    borderRadius: "8px",
    padding: "10px 20px",
    fontSize: "14px",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "all 0.3s ease",
    boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.2)",
    marginTop: "10px",
  },
  addButton: {
    display: "block",
    margin: "20px auto",
    backgroundColor: "#81a1c1", // Matches student name blue
    color: "#2c2c2c", // Dark background to contrast with button
    border: "none",
    borderRadius: "8px",
    padding: "12px 24px",
    fontSize: "16px",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "all 0.3s ease",
    boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.3)",
  },
  noDataMessage: {
    textAlign: "center",
    fontSize: "18px",
    color: "#cccccc", // Neutral gray for the message
  },
};

// Export component
export default AllStudentsView;
