// /*==================================================
// EditStudentView.js
//
// The Views component responsible for rendering the edit student form.
// Allows users to update student details such as name, email, GPA, etc.
// Includes error handling and redirects back to the student view after submission.
// ==================================================*/

import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import axios from "axios";

const EditStudentView = () => {
  const { id } = useParams(); // Get student ID from the URL
  const history = useHistory();

  const [studentData, setStudentData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    imageurl: "",
    gpa: "",
    campusId: "",
  });
  const [error, setError] = useState(""); // Error state

  // Fetch student data when the component mounts
  useEffect(() => {
    const fetchStudentData = async () => {
      try {
        const response = await axios.get(`/api/students/${id}`);
        setStudentData(response.data); // Set student data to state
      } catch (error) {
        setError("Error fetching student data.");
      }
    };
    fetchStudentData();
  }, [id]);

  // Handle form input changes
  const handleChange = (e) => {
    setStudentData({ ...studentData, [e.target.name]: e.target.value });
  };

  // Handle saving the changes to the student
  const handleSaveChanges = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/api/students/${id}`, studentData); // Update student
      history.push(`/student/${id}`); // Redirect back to student view
    } catch (error) {
      setError("Error saving student data.");
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Edit Student</h1>
      {error && <p style={styles.error}>{error}</p>}
      <form onSubmit={handleSaveChanges} style={styles.form}>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Name:</label>
          <input
            type="text"
            name="firstname"
            value={studentData.firstname}
            onChange={handleChange}
            required
            style={styles.input}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Last Name:</label>
          <input
            type="text"
            name="lastname"
            value={studentData.lastname}
            onChange={handleChange}
            required
            style={styles.input}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Email:</label>
          <input
            type="email"
            name="email"
            value={studentData.email}
            onChange={handleChange}
            required
            style={styles.input}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>Image URL:</label>
          <input
            type="text"
            name="imageurl"
            value={studentData.imageurl}
            onChange={handleChange}
            style={styles.input}
          />
        </div>
        <div style={styles.inputGroup}>
          <label style={styles.label}>GPA:</label>
          <input
            type="number"
            name="gpa"
            value={studentData.gpa}
            onChange={handleChange}
            step="0.1"
            min="0.0"
            max="4.0"
            required
            style={styles.input}
          />
        </div>
        <div style={styles.buttonGroup}>
          <button type="submit" style={styles.saveButton}>
            Save Changes
          </button>
          <button
            type="button"
            onClick={() => history.push(`/student/${id}`)}
            style={styles.cancelButton}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

// Styling
const styles = {
  container: {
    backgroundImage: "url('https://cdn.impossibleimages.ai/wp-content/uploads/2023/08/17184052/F0clq6ws5ZtFU8IG4Yz8p1QN4SJdMTXAZPGsEidOFd9N40YQWC-scaled.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "40px",
    textAlign: "center",
  },
  title: {
    fontSize: "28px",
    fontWeight: "bold",
    color: "#444",
    marginBottom: "20px",
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    padding: "10px 20px",
    borderRadius: "10px",
    display: "inline-block",
  },
  error: {
    color: "red",
    textAlign: "center",
    marginBottom: "10px",
  },
  form: {
    backgroundColor: "rgba(255, 255, 255, 0.85)",
    padding: "25px",
    borderRadius: "12px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    maxWidth: "400px",
    width: "100%",
    textAlign: "left",
  },
  inputGroup: {
    marginBottom: "15px",
  },
  label: {
    display: "block",
    fontWeight: "bold",
    marginBottom: "5px",
    color: "#555",
  },
  input: {
    width: "100%",
    padding: "8px",
    borderRadius: "6px",
    border: "1px solid #ddd",
    fontSize: "14px",
  },
  buttonGroup: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "20px",
  },
  saveButton: {
    backgroundColor: "#4CAF50",
    color: "#fff",
    padding: "8px 16px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "bold",
  },
  cancelButton: {
    backgroundColor: "#e0e0e0",
    color: "#333",
    padding: "8px 16px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "bold",
  },
};

export default EditStudentView;
