// /*==================================================
// StudentView.js

// The Views component is responsible for rendering web page with data provided by the corresponding Container component.
// It constructs a React component to display the single student view page.
// ================================================== */
// StudentView.js

// The Views component is responsible for rendering a web page with data provided by the corresponding Container component.
// It constructs a React component to display the single student view page.

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const StudentView = ({ student, campus, fetchStudent, onDeleteStudent }) => {
  const [campuses, setCampuses] = useState([]); // List of all campuses
  const [selectedCampusId, setSelectedCampusId] = useState(""); // Store selected campus ID
  const [isCampusDropdownVisible, setIsCampusDropdownVisible] = useState(false); // Track dropdown visibility
  const [success, setSuccess] = useState(""); // Success messages
  const [error, setError] = useState(""); // Error messages

  // Fetch all campuses when the component is mounted
  useEffect(() => {
    const fetchCampuses = async () => {
      try {
        const response = await axios.get("/api/campuses");
        setCampuses(response.data);
      } catch (error) {
        console.error("Error fetching campuses:", error);
        setError("Failed to fetch campuses.");
      }
    };
    fetchCampuses();
  }, []);

  // Handle removing student from campus and refreshing the page
  const handleRemoveFromCampus = async () => {
    const confirmation = window.confirm(
      "Are you sure you want to remove this student from this campus?"
    );
    if (confirmation) {
      try {
        // Send the request to remove the student from campus
        await axios.put(`/api/students/${student.id}/`, {
          campusId: null, // Set campusId to null
        });

        setSuccess("Student removed from campus successfully!"); // Update success message
        fetchStudent(student.id); // Refetch the student data
      } catch (error) {
        console.error("Error removing student from campus:", error);
        setError("An error occurred while removing the student from campus."); // Update error message
      }
    }
  };

  // Handle adding a student to a campus
  const handleAddCampus = () => {
    if (student.campusId) {
      setError("Campus can't be added, student is already enrolled."); // Show error if already enrolled
    } else {
      setIsCampusDropdownVisible(true); // Show the dropdown if the student doesn't have a campus
    }
  };

  // Handle selecting a campus from the dropdown
  const handleCampusSelect = (event) => {
    setSelectedCampusId(event.target.value);
  };

  // Handle adding the student to the selected campus
  const handleAddCampusToStudent = async () => {
    if (!selectedCampusId) {
      setError("Please select a campus."); // Show error if no campus selected
      return;
    }

    try {
      // Send a request to update the student's campusId
      await axios.put(`/api/students/${student.id}/`, {
        campusId: selectedCampusId,
      });

      setSuccess("Student successfully added to the campus!"); // Show success message
      fetchStudent(student.id); // Refetch the student to get the updated data
      setIsCampusDropdownVisible(false); // Hide the dropdown
      setSelectedCampusId(""); // Clear selected campus
    } catch (error) {
      console.error("Error adding student to campus:", error);
      setError("An error occurred while adding the student to campus."); // Show error message
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.headerContainer}>
        <div style={styles.infoContainer}>
          <h1 style={styles.name}>{`${student.firstname} ${student.lastname}`}</h1>
          <p style={styles.email}>Email: {student.email}</p>
          <p style={styles.gpa}>GPA: {student.gpa || "N/A"}</p>
        </div>
        <img
          src={student.imageurl || "https://via.placeholder.com/150"}
          alt={`${student.firstname} ${student.lastname}`}
          style={styles.image}
        />
      </div>

      <h2 style={styles.sectionTitle}>Enrolled Campus:</h2>
      {student.campus ? (
        <div style={styles.campusContainer}>
          <Link to={`/campus/${student.campus.id}`} style={styles.campusLink}>
            <h3 style={styles.campusName}>{student.campus.name}</h3>
          </Link>
          <p style={styles.campusAddress}>{student.campus.address}</p>
          <button
            style={styles.removeButton}
            onClick={handleRemoveFromCampus}
          >
            Remove from Campus
          </button>
        </div>
      ) : (
        <p style={styles.noCampusMessage}>Student not enrolled in any campus.</p>
      )}

      {success && <p style={styles.successMessage}>{success}</p>}
      {error && <p style={styles.errorMessage}>{error}</p>}

      <button style={styles.addButton} onClick={handleAddCampus}>Add Campus</button>

      {isCampusDropdownVisible && (
        <div style={styles.dropdownContainer}>
          <h3>Select a Campus</h3>
          <select
            style={styles.dropdown}
            onChange={handleCampusSelect}
            value={selectedCampusId}
          >
            <option value="">Select a campus</option>
            {campuses.map((campus) => (
              <option key={campus.id} value={campus.id}>
                {campus.name}
              </option>
            ))}
          </select>
          <button
            style={styles.addButton}
            onClick={handleAddCampusToStudent}
          >
            Add to Campus
          </button>
        </div>
      )}

      <Link to={`/editstudent/${student.id}`} style={styles.editLink}>
        <button style={styles.editButton}>Edit Student</button>
      </Link>
    </div>
  );
};

const styles = {
  container: {
    padding: "20px",
    maxWidth: "800px",
    margin: "auto",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
  },
  headerContainer: {
    display: "flex",
    alignItems: "center",
    marginBottom: "20px",
  },
  infoContainer: {
    flex: 1,
  },
  name: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#333",
  },
  email: {
    fontSize: "16px",
    color: "#666",
  },
  gpa: {
    fontSize: "16px",
    color: "#666",
  },
  image: {
    width: "150px",
    height: "150px",
    borderRadius: "50%",
    objectFit: "cover",
    border: "2px solid #ddd",
  },
  sectionTitle: {
    fontSize: "20px",
    color: "#444",
    marginBottom: "10px",
  },
  campusContainer: {
    marginBottom: "20px",
  },
  campusLink: {
    textDecoration: "none",
  },
  campusName: {
    fontSize: "18px",
    fontWeight: "bold",
    color: "#2c3e50",
  },
  campusAddress: {
    fontSize: "16px",
    color: "#7f8c8d",
  },
  removeButton: {
    backgroundColor: "#e74c3c",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    padding: "8px 12px",
    cursor: "pointer",
  },
  noCampusMessage: {
    color: "#888",
    fontStyle: "italic",
  },
  successMessage: {
    color: "green",
    fontSize: "14px",
  },
  errorMessage: {
    color: "red",
    fontSize: "14px",
  },
  addButton: {
    backgroundColor: "#3498db",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    padding: "8px 12px",
    cursor: "pointer",
    marginBottom: "20px",
  },
  dropdownContainer: {
    marginBottom: "20px",
  },
  dropdown: {
    width: "100%",
    padding: "8px",
    borderRadius: "4px",
    border: "1px solid #ddd",
    marginBottom: "10px",
  },
  editLink: {
    textDecoration: "none",
  },
  editButton: {
    backgroundColor: "#2ecc71",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    padding: "8px 12px",
    cursor: "pointer",
  },
};

export default StudentView;
