/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */

import { useState } from "react";
import { Link } from "react-router-dom";
import { addStudentThunk } from "../../store/thunks";

const CampusView = (props) => {
  const {
    campus,
    allStudents,
    updateStudent,
    removeStudentFromCampus,
    fetchCampus,
  } = props;

  // Track whether to show the forms
  const [showNewStudentForm, setShowNewStudentForm] = useState(false);
  const [showExistingStudentForm, setShowExistingStudentForm] = useState(false);

  // Handle new student form state
  const [newStudent, setNewStudent] = useState({
    firstname: "",
    lastname: "",
    email: "",
    gpa: "",
    campusId: campus.id,
  });

  const [selectedStudent, setSelectedStudent] = useState("");

  // State to track edit mode
  const [isEditing, setIsEditing] = useState(false);

  // Handle new student form submission
  const handleNewStudentSubmit = async (e) => {
    e.preventDefault();
    const addedStudent = await addStudentThunk(newStudent); // Call the thunk
    if (addedStudent) {
      setNewStudent({ firstname: "", lastname: "", email: "", gpa: "", campusId: campus.id });
      setShowNewStudentForm(false); // Hide the form
    }
  };

  // Handle adding existing student
  const handleAddExistingStudent = (e) => {
    e.preventDefault();
    if (selectedStudent) {
      updateStudent(selectedStudent, { campusId: campus.id });
      setSelectedStudent("");
      setShowExistingStudentForm(false); // Hide the form after submission
    }
  };

  // Handle removing a student with confirmation
  const handleRemoveStudent = async (studentId) => {
    const confirmation = window.confirm("Are you sure you want to remove this student?");
    if (confirmation) {
      await removeStudentFromCampus(studentId, campus.id); // Remove the student
      await fetchCampus(campus.id); // Re-fetch the updated campus data
    }
  };

  // Handle canceling the edit
  const handleCancelEdit = () => {
    setIsEditing(false); // Exit edit mode
  };

  return (
    <div>
      {!isEditing ? (
        <>
          <h1>{campus.name}</h1>
          <p>{campus.address}</p>
          <p>{campus.description}</p>
          <img
            src={campus.imageUrl || "https://via.placeholder.com/150"}
            alt={`${campus.name} campus`}
            style={{ width: "300px", height: "auto" }}
          />
          <br />

          <h2>Enrolled Students:</h2>
          {campus.students && campus.students.length > 0 ? (
            campus.students.map((student) => (
              <div key={student.id}>
                <Link to={`/student/${student.id}`}>
                  <h3>{`${student.firstname} ${student.lastname}`}</h3>
                </Link>
                <button onClick={() => handleRemoveStudent(student.id)}>
                  Remove Student
                </button>
              </div>
            ))
          ) : (
            <p>No students are currently enrolled at this campus.</p>
          )}

          <br />

          {/* Buttons to trigger the forms */}
          <button onClick={() => setShowNewStudentForm(!showNewStudentForm)}>
            {showNewStudentForm ? "Cancel Add New Student" : "Add New Student"}
          </button>
          <br />
          <button onClick={() => setShowExistingStudentForm(!showExistingStudentForm)}>
            {showExistingStudentForm ? "Cancel Add Existing Student" : "Add Existing Student"}
          </button>

          {/* New Student Form */}
          {showNewStudentForm && (
            <form onSubmit={handleNewStudentSubmit}>
              <h2>Add New Student</h2>
              <label>
                First Name:
                <input
                  type="text"
                  value={newStudent.firstname}
                  onChange={(e) => setNewStudent({ ...newStudent, firstname: e.target.value })}
                  required
                />
              </label>
              <br />
              <label>
                Last Name:
                <input
                  type="text"
                  value={newStudent.lastname}
                  onChange={(e) => setNewStudent({ ...newStudent, lastname: e.target.value })}
                  required
                />
              </label>
              <br />
              <label>
                Email:
                <input
                  type="email"
                  value={newStudent.email}
                  onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
                  required
                />
              </label>
              <br />
              <label>
                GPA:
                <input
                  type="number"
                  step="0.01"
                  value={newStudent.gpa}
                  onChange={(e) => setNewStudent({ ...newStudent, gpa: e.target.value })}
                />
              </label>
              <br />
              <button type="submit">Add Student</button>
            </form>
          )}

          {/* Existing Student Dropdown */}
          {showExistingStudentForm && (
            <form onSubmit={handleAddExistingStudent}>
              <h2>Add Existing Student</h2>
              <label>
                Select Student:
                <select
                  value={selectedStudent}
                  onChange={(e) => setSelectedStudent(e.target.value)}
                  required
                >
                  <option value="">-- Select a Student --</option>
                  {allStudents &&
                    allStudents
                      .filter((student) => !student.campusId) // Only show students not already enrolled
                      .map((student) => (
                        <option key={student.id} value={student.id}>
                          {`${student.firstname} ${student.lastname}`}
                        </option>
                      ))}
                </select>
              </label>
              <br />
              <button type="submit">Add to Campus</button>
            </form>
          )}

          <br />
          <button onClick={() => setIsEditing(true)}>Edit Campus</button>
        </>
      ) : (
        <>
          <h1>Editing {campus.name}</h1>
          {/* Add edit campus form or functionality here */}
          <form>
            <label>
              Name:
              <input type="text" defaultValue={campus.name} />
            </label>
            <br />
            <label>
              Address:
              <input type="text" defaultValue={campus.address} />
            </label>
            <br />
            <label>
              Description:
              <textarea defaultValue={campus.description}></textarea>
            </label>
            <br />
            <label>
              Image URL:
              <input type="text" defaultValue={campus.imageUrl} />
            </label>
            <br />
            <button type="submit">Save Changes</button>
            <button type="button" onClick={handleCancelEdit}>Cancel Edit</button>
          </form>
        </>
      )}
    </div>
  );
};

export default CampusView;
