/*==================================================
EditCampusView.js

This component is responsible for rendering the edit form for a specific campus. 
It allows users to update the campus's name, address, description, and image URL.
================================================== */

import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import Header from "./Header"; // Importing the Header component

const EditCampusView = ({ fetchCampus, updateCampus, campus }) => {
  const { id } = useParams(); // Get campus ID from the route
  const history = useHistory();
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    description: "",
    imageUrl: "",
  });

  // Fetch the campus data when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      await fetchCampus(id);
    };
    fetchData();
  }, [id, fetchCampus]);

  // Update form data when the campus data is fetched
  useEffect(() => {
    if (campus) {
      setFormData({
        name: campus.name || "",
        address: campus.address || "",
        description: campus.description || "",
        imageUrl: campus.imageUrl || "",
      });
    }
  }, [campus]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateCampus(id, formData); // Call the updateCampus function
    history.push(`/campus/${id}`); // Navigate back to the Campus View
  };

  const handleCancel = () => {
    history.push(`/campus/${id}`); // Navigate back to the Campus page without saving
  };

  return (
    <div style={styles.container}>
      <Header /> {/* Add the Header at the top */}
      <div style={styles.formContainer}>
        <h1 style={styles.title}>Edit Campus</h1>
        <form onSubmit={handleSubmit} style={styles.form}>
          <label style={styles.label}>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </label>
          <label style={styles.label}>
            Address:
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </label>
          <label style={styles.label}>
            Description:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              style={styles.textarea}
            />
          </label>
          <label style={styles.label}>
            Image URL:
            <input
              type="text"
              name="imageUrl"
              value={formData.imageUrl}
              onChange={handleChange}
              style={styles.input}
            />
          </label>
          <div style={styles.buttonGroup}>
            <button type="submit" style={styles.saveButton}>
              Save Changes
            </button>
            <button type="button" onClick={handleCancel} style={styles.cancelButton}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// CSS Styles
const styles = {
  container: {
    backgroundImage: "url('https://media.cntraveler.com/photos/5b6820739dc0d5057c463bb9/master/w_2580%2Cc_limit/Bard%2520College%2520GettyImages-475706736.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    minHeight: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    padding: "20px",
  },
  formContainer: {
    backgroundColor: "rgba(255, 255, 255, 0.9)", // Slightly opaque white for contrast
    borderRadius: "12px",
    padding: "20px",
    width: "100%",
    maxWidth: "400px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)", // Subtle shadow
  },
  title: {
    fontSize: "24px",
    fontWeight: "bold",
    textAlign: "center",
    color: "#2c3e50", // Muted navy
    marginBottom: "20px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
  },
  label: {
    fontSize: "14px",
    fontWeight: "600",
    color: "#2c3e50",
    marginBottom: "8px",
  },
  input: {
    width: "100%",
    padding: "8px",
    marginBottom: "20px",
    border: "1px solid #ccc",
    borderRadius: "6px",
    fontSize: "14px",
  },
  textarea: {
    width: "100%",
    padding: "8px",
    marginBottom: "20px",
    border: "1px solid #ccc",
    borderRadius: "6px",
    fontSize: "14px",
    minHeight: "80px",
  },
  buttonGroup: {
    display: "flex",
    justifyContent: "space-between",
  },
  saveButton: {
    backgroundColor: "#27ae60",
    color: "#fff",
    border: "none",
    padding: "10px 15px",
    borderRadius: "6px",
    fontSize: "14px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
  cancelButton: {
    backgroundColor: "#c0392b",
    color: "#fff",
    border: "none",
    padding: "10px 15px",
    borderRadius: "6px",
    fontSize: "14px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
};

export default EditCampusView;
