/*==================================================
EditCampusView.js

This component is responsible for rendering the edit form for a specific campus. 
It allows users to update the campus's name, address, description, and image URL.
================================================== */

import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";

const EditCampusView = ({ fetchCampus, updateCampus, campus }) => {
  const { id } = useParams(); // Get campus ID from the route
  const history = useHistory();
  const [formData, setFormData] = useState({
    name: "",
    address: "",
    description: "",
    imageUrl: "",
  });

  // Fetch the campus data when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      await fetchCampus(id);
    };
    fetchData();
  }, [id, fetchCampus]);

  // Update form data when the campus data is fetched
  useEffect(() => {
    if (campus) {
      setFormData({
        name: campus.name || "",
        address: campus.address || "",
        description: campus.description || "",
        imageUrl: campus.imageUrl || "",
      });
    }
  }, [campus]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateCampus(id, formData); // Call the updateCampus function
    history.push(`/campus/${id}`); // Navigate back to the Campus View
  };

  const handleCancel = () => {
    history.push(`/campus/${id}`); // Navigate back to the Campus page without saving
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Edit Campus</h1>
      </div>
      <form onSubmit={handleSubmit} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>
            Address:
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              style={styles.input}
            />
          </label>
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>
            Description:
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              style={styles.textarea}
            />
          </label>
        </div>
        <div style={styles.formGroup}>
          <label style={styles.label}>
            Image URL:
            <input
              type="text"
              name="imageUrl"
              value={formData.imageUrl}
              onChange={handleChange}
              style={styles.input}
            />
          </label>
        </div>
        <div style={styles.buttonGroup}>
          <button type="submit" style={styles.saveButton}>
            Save Changes
          </button>
          <button type="button" onClick={handleCancel} style={styles.cancelButton}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

// CSS Styles
const styles = {
  container: {
    backgroundColor: "#f6f6f6", // Neutral light gray background
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px",
  },
  header: {
    marginBottom: "20px",
    textAlign: "center",
  },
  title: {
    fontSize: "36px",
    fontWeight: "bold",
    color: "#2c3e50", // Muted navy
    marginBottom: "10px",
  },
  form: {
    backgroundColor: "#ffffff", // White form background
    padding: "30px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.1)", // Subtle shadow
    maxWidth: "500px",
    width: "100%",
  },
  formGroup: {
    marginBottom: "20px",
  },
  label: {
    display: "block",
    fontSize: "16px",
    fontWeight: "600",
    color: "#34495e", // Dark gray-blue
    marginBottom: "8px",
  },
  input: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#f9f9f9", // Slightly lighter input background
  },
  textarea: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    backgroundColor: "#f9f9f9",
    minHeight: "80px",
  },
  buttonGroup: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "20px",
  },
  saveButton: {
    backgroundColor: "#27ae60", // Muted green
    color: "#ffffff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "8px",
    fontSize: "14px",
    fontWeight: "600",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.1)",
  },
  cancelButton: {
    backgroundColor: "#c0392b", // Muted red
    color: "#ffffff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "8px",
    fontSize: "14px",
    fontWeight: "600",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
    boxShadow: "0 3px 6px rgba(0, 0, 0, 0.1)",
  },
};

export default EditCampusView;
